d-svg .active3{fill:#bfc7ff;stroke:#534fbc;}#mermaid-svg .activeText0,#mermaid-svg .activeText1,#mermaid-svg .activeText2,#mermaid-svg .activeText3{fill:black!important;}#mermaid-svg .done0,#mermaid-svg .done1,#mermaid-svg .done2,#mermaid-svg .done3{stroke:grey;fill:lightgrey;stroke-width:2;}#mermaid-svg .doneText0,#mermaid-svg .doneText1,#mermaid-svg .doneText2,#mermaid-svg .doneText3{fill:black!important;}#mermaid-svg .crit0,#mermaid-svg .crit1,#mermaid-svg .crit2,#mermaid-svg .crit3{stroke:#ff8888;fill:red;stroke-width:2;}#mermaid-svg .activeCrit0,#mermaid-svg .activeCrit1,#mermaid-svg .activeCrit2,#mermaid-svg .activeCrit3{stroke:#ff8888;fill:#bfc7ff;stroke-width:2;}#mermaid-svg .doneCrit0,#mermaid-svg .doneCrit1,#mermaid-svg .doneCrit2,#mermaid-svg .doneCrit3{stroke:#ff8888;fill:lightgrey;stroke-width:2;cursor:pointer;shape-rendering:crispEdges;}#mermaid-svg .milestone{transform:rotate(45deg) scale(0.8,0.8);}#mermaid-svg .milestoneText{font-style:italic;}#mermaid-svg .doneCritText0,#mermaid-svg .doneCritText1,#mermaid-svg .doneCritText2,#mermaid-svg .doneCritText3{fill:black!important;}#mermaid-svg .vert{stroke:navy;}#mermaid-svg .vertText{font-size:15px;text-anchor:middle;fill:navy!important;}#mermaid-svg .activeCritText0,#mermaid-svg .activeCritText1,#mermaid-svg .activeCritText2,#mermaid-svg .activeCritText3{fill:black!important;}#mermaid-svg .titleText{text-anchor:middle;font-size:18px;fill:#333;font-family:"trebuchet ms",verdana,arial,sans-serif;}#mermaid-svg :root{--mermaid-font-family:"trebuchet ms",verdana,arial,sans-serif;}""";

